import React from "react";
import { useEffect, useState } from "react";
import Map, {
  NavigationControl,
  Marker,
  GeolocateControl,
  FullscreenControl,
  ScaleControl,
  MapProvider,
  Popup,
} from "react-map-gl";
import maplibregl from "maplibre-gl";
import "maplibre-gl/dist/maplibre-gl.css";
const MyMap = () => {
  const [showPopup, setShowPopup] = useState(true);
  const [location, setLocation] = useState({
    latitude: 12.958184,
    longitude: 77.6421466,
  });
  useEffect(() => {
    const success = (position) => {
      setLocation({
        latitude: position.coords.latitude,
        longitude: position.coords.longitude,
      });
    };
    const error = () => {
      console.log("Error while accessing location...");
    };
    navigator.geolocation.getCurrentPosition(success, error);
    console.log(location.lat, location.lng);
  }, []);
  const clickLoc = () => {
    document.getElementsByClassName("maplibregl-ctrl-geolocate")[0].click();
    console.log(location);
  };
  window.onload = clickLoc;
  return (
    <div className="MyMap">
      <Map
        mapLib={maplibregl}
        initialViewState={{
          longitude: location.longitude,
          latitude: location.latitude,
          zoom: 3.5,
        }}
        style={{ width: "100%", height: "100%" }}
        mapStyle="https://api.maptiler.com/maps/streets/style.json?key=MHdzunc1wzN53Dd2CzRS"
      >
        <NavigationControl />
        <GeolocateControl showAccuracyCircle={false} />
        <ScaleControl />
        <Marker latitude={12.958184} longitude={77.6421466} color="red"></Marker>
          <Popup
            longitude={location.longitude}
            latitude={location.latitude}
            anchor="bottom"
            closeButton={false}
            closeOnClick={false}
          >
            You are here
          </Popup>
          <Popup
            longitude={location.longitude}
            latitude={location.latitude}
            anchor="top"
            closeButton={false}
            closeOnClick={false}
          >
            Pin
          </Popup>
      </Map>
    </div>
  );
  // return (
  //   <div className="MyMap">
  //     <Map
  //       mapLib={maplibregl}
  //       initialViewState={{
  //         latitude: location.latitude,
  //         longitude: location.longitude,
  //         zoom: 10,
  //       }}
  //       style={{ width: "100%", height: "100%" }}
  //       mapStyle="https://api.maptiler.com/maps/streets-v2/style.json?key=MHdzunc1wzN53Dd2CzRS"
  //     >
  //       <Marker latitude={12.942786} longitude={77.687524} color="red"></Marker>
  //       {/* <Marker
  //         latitude={10.942786}
  //         longitude={77.687524}
  //         color="blue"
  //       ></Marker> */}
  //       <NavigationControl />
  //       <GeolocateControl showAccuracyCircle={false} />
  //       <FullscreenControl />
  //       <ScaleControl />
  //       <Popup
  //         longitude={location.latitude}
  //         latitude={location.longitude}
  //         anchor="bottom"
  //       >
  //         You are here
  //       </Popup>
  //     </Map>
  //   </div>
  // );
};

export default MyMap;
